mapper.select:
select dm_no as dmNo, send_mem_id as sendMemId, recv_mem_id as recvMemId, m_title as mTitle, m_content as mContent, m_datetime as mDatetime, m_rectime as mRectime, read_ck as readCk, warning, category, category_no as categoryNo from message where send_mem_id = 'admin' and recv_mem_id =#{memberId} and category = #{category}
and category_no = #{categoryNo}
and category_no = #{categoryNo}
order by read_ck desc, dm_no desc
